mapper.select:
SELECT COUNT(*)
FROM SUMMONER_MATCH_INFO
WHERE ACCOUNT_ID = #{accountId}
AND   GAME_ID = #{gameId}

mapper.insert:
INSERT INTO SUMMONER_MATCH_INFO
(
	ACCOUNT_ID
	, GAME_ID
	, PLATFORM_ID
	, CHAMPION
	, QUEUE
	, SEASON
	, TIME_STAMP
	, ROLE
	, LANE
	, REG_DATE
) VALUES (
	#{accountId}
	, #{gameId}
	, #{platformId}
	, #{champion}
	, #{queue}
	, #{season}
	, #{timeStamp}
	, #{role}
	, #{lane}
	, NOW()
)
